/**
*  description: 自动播放、下一集、关闭答题窗口、刷智慧树网课
*  use-method: 打开智慧树播放课程界面 按F12 -> Console -> 粘贴本代码 ->按回车键  
*  use-method: 视频左上方出现一个图标点击图标开始刷课 显示‘已开’脚本开始监听 再次点击图标关闭
*/
//查找节点
var tips = document.getElementsByClassName("exploreTip");
//创建新节点
var option = document.createElement("div");
//添加样式
option.style = "color:DarkOrange;text-align:center;line-height:90px;font-size:30px;background-image: url(data:image/jpg;base64,); background-repeat: no-repeat; background-size: 100%; width: 90px; height: 90px; border-radius: 45px;position:fixed;left:60px;top:200px; z-index:999;"
//添加点击事件
option.setAttribute("onClick", "StartOrStop()");
option.setAttribute("id", "rains");
option.innerText = "Rains";
//插入节点到页面
tips[0].parentNode.insertBefore(option, tips[0]);
//定时器
var timer;
//初始状态
var startstatus = 0;
//次数
var number = 0;
//函数
function start() {
    if (number % 200 == 0) {
        console.clear();
    }
    number += 1;
    //获取进度条
    var pass = document.getElementsByClassName("passTime");
    //获取答题框状态
    var dialog = document.getElementsByClassName("wrap_popboxes  wrap_popchapter")[0];
    //点击关闭按钮关闭答题框
    if (dialog != undefined) {
        document.getElementsByClassName("popbtn_cancel")[0].click();
    }
    //判断播放进度是否大于83%
    if (parseInt(pass[0].style.width) > 83) {
        //切换下一个视频
        document.getElementById("nextBtn").click();
    }
    console.log("执行第" + number + "次");
}
function StartOrStop() {
    //获取播放状态
    var play = document.getElementsByClassName("bigPlayButton pointer")[0];
    if (startstatus == 0) {
        //开始脚本
        timer = setInterval(start, 3000);
        //已经暂停 点击开始按钮 
        if (play.style.display == "block") {
            play.click();
        }
        startstatus = 1;
        var rains = document.getElementById("rains");
        rains.innerText = "已开";
        rains.style.color = "blue";
    } else if (startstatus == 1) {
        //停止脚本
        clearInterval(timer);
        //已经播放 暂停播放
        if (play.style.display == "none") {
            play.click();
        }
        startstatus = 0;
        var rains = document.getElementById("rains");
        rains.innerText = "已关";
        rains.style.color = "red";
    }
}